
public class priitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte minByteValue = Byte.MIN_VALUE;
		byte maxByteValue = Byte.MAX_VALUE;
		System.out.println("minimum byte value is ..." + minByteValue);
		System.out.println("max byte value is ..." + maxByteValue);
		 
		int minValue = Integer.MIN_VALUE;
		int maxvalue  = Integer.MAX_VALUE;
		System.out.println("Integer min is : " + minValue);

		System.out.println("Integer max is : " + maxvalue);

		System.out.println("long min value is : " + Long.MIN_VALUE);
		
	}
	

}
